function orderFood(){

    let food = document.querySelector("#food").value;

    let status = "open";

    let foodPromise = new Promise(function(resolve,reject){

        let time = Math.random() *10000;
        
        if(status == "close")
        {
            reject("Shop Closed, Ghar Jao")
        }

        else
        {
            setTimeout(function(){

                if(status == "open")
                {
                    resolve(food);
                }


            }, time);
        }
        
        
    });

    foodPromise.then(function(res){
        console.log(`${res} is ready`)

        if(res == "fries")
        {
            document.querySelector("#image").innerHTML = "";
            let pic = document.createElement("img");
            pic.src="data:image/jpeg;base64,";
            
            let name = document.createElement("h1");
            name.innerText = "Enjoy Your Fries";

            document.querySelector("#image").append(pic, name);
        }

        else if(res == "burger")
        {
            document.querySelector("#image").innerHTML = "";
            let pic = document.createElement("img");
            pic.src= "data:image/jpeg;base64,"
            
            let name = document.createElement("h1");
            name.innerText = "Enjoy Your Burger";

            document.querySelector("#image").append(pic, name);
        }

        else if(res == "coke")
        {
            document.querySelector("#image").innerHTML = "";
            let pic = document.createElement("img");
            pic.src= "data:image/jpeg;base64,";

            let name = document.createElement("h1");
            name.innerText = "Enjoy Your Coke";

            document.querySelector("#image").append(pic, name);
        }

        else if(res == "wadapao")
        {
            document.querySelector("#image").innerHTML = "";
            let pic = document.createElement("img");
            pic.src= "data:image/jpeg;base64,";

            let name = document.createElement("h1");
            name.innerText = "Enjoy Your Wadapao";

            document.querySelector("#image").append(pic, name);
        }

    })

    foodPromise.catch(function(error){
        console.log(error)

        if(status == "close")
        {
            document.querySelector("#image").innerHTML = "";
            let pic = document.createElement("img");
            pic.src= "data:image/jpeg;base64,";

            let name = document.createElement("h1");
            name.innerText = "Shop Is Closed";

            document.querySelector("#image").append(pic, name);
        }

    })
}